(30251, 'wppb_user_emailc_reset_email_content', '<p>Someone requested that the password be reset for the following account: {{site_name}}<br/>\nUsername: {{username}}</p>\n<p>If this was a mistake, just ignore this email and nothing will happen.</p>\n<p>To reset your password, visit the following address:<br/>\n{{{reset_link}}}</p>\n', 'yes'),
(30252, 'wppb_user_emailc_reset_success_email_subject', 'Пароль було успішно змінено для [{{site_name}}]', 'yes'),
