(30257, 'wppb_user_emailc_change_email_address_request_content', '<h3>Hi {{username}},</h3>\n<p>There is an email address change request on {{site_name}}.</p>\n<p>To change your email address click on: {{{user_email_change_link}}}</p>\n<p>Regards,<br>\nAll at {{site_name}}<br>\n<a href=\"{{site_url}}\">{{site_url}}</a></p>', 'yes'),
(30258, 'wppb_user_emailc_change_email_address_subject', '[{{site_name}}] Повідомлення про зміну адреси електронної пошти', 'yes'),
(30259, 'wppb_user_emailc_change_email_address_enabled', 'on', 'yes'),
(30260, 'wppb_user_emailc_change_email_address_content', '<h3>Hi {{username}},</h3>\n<p>This notice confirms that your email was changed on {{site_name}}.</p>\n<p>If you did not change your email, please contact the Site Administrator at arhibober@gmail.com</p>\n<p>This email has been sent to {{user_email}}</p>\n<p>Regards,<br>\nAll at {{site_name}}<br>\n<a href=\"{{site_url}}\">{{site_url}}</a></p>', 'yes'),
